INSERT INTO tb_pizzas (sabor_pizza, desc_pizza, prec_unit) VALUES ('Frango c/ Catupiry', 'Deliciosa pizza de frango com Catupiry, crocante, nem muito fina e nem grossa, elaborada com massa de longa fermentação, molho de tomate artesanal, mussarela magra e saborosa, frango desfiado cozido no molho de tomate , Catupiry e orégano.', 40.0);